import './HomeUser1.css';

const HomeUser1 = () => {
  // Initialize state to hide both elements initially
  const [isOpen, setIsOpen] = useState("none");
 
  const handleToggle = () => {
    setIsOpen("flex")
  
 
  };
  
  isOpen=="flex"?setIsOpen("block"):"none"

  console.log(isOpen); // Log the current values for debugging

  return (
    <div className='Container-Screen-Shoot'>
      <div className='Screen'>
 
        <div className='banner-top' style={{ display: isOpen }}></div>
        <div className='banner' onClick={handleToggle}>
        Win
        </div>
      </div>
    </div>
  );
};

export default HomeUser1;
