import React, { useState } from 'react';
import './HomeUser1.css';

const HomeUser1 = () => {
  // Initialize state to control visibility
  const [isOpen, setIsOpen] = useState("block");

  // Toggle the state between "block" and "none"
  const handleToggle = () => {
    setIsOpen(prevIsOpen => prevIsOpen === "block" ? "none" : "block");
  };

  console.log(isOpen); // Log the current value of isOpen for debugging

  return (
    <div className='Container-Screen-Shoot'>
      {/* Toggle visibility based on isOpen */}
      <div className='Screen' style={{ display: isOpen }}>
        <div className='banner-top'></div>
        <div className='banner' onClick={handleToggle}>
          {isOpen === "block" ? "Hide" : "Show"} BannerIcon Windows
        </div>
      </div>
    </div>
  );
};

export default HomeUser1;
