import React from 'react';
import "./Card2.css";
import { Nahdi_Gayth } from '../context/GlobalContext';

const Card2 = ({ id, title, img }) => {
    const { FunctionDispatch, State } = Nahdi_Gayth();
    
    const HandelWIthThisButton = (e) => {
        e.preventDefault();
        
        // Get the current basket data
        const currentBasket = State.Basket_Data || []; // Adjust this based on how you access the state

        // Check if the item already exists in the basket
        const itemExists = currentBasket.some(item => item.id === id);

        if (itemExists) {
            console.log("Item already in the basket");
            return; // Exit if the item already exists
        }

        // Dispatch action to add the item to the basket
        FunctionDispatch({
            type: "PUSH_TO_BASKET",
            payload: {
                id,
                title,
                img,
            }
        });
    };

    return (
        <div className='card1'>
            <div className='card1-img'><img src={img} alt={title} /> </div>
            <div className='card1-img-title'>{title}</div>

            <button className="button" type="button" onClick={HandelWIthThisButton}>
                <span className="button__text">Download</span>
                <span className="button__icon">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 35 35" id="bdd05811-e15d-428c-bb53-8661459f9307" data-name="Layer 2" className="svg">
                        <path d="M17.5,22.131a1.249,1.249,0,0,1-1.25-1.25V2.187a1.25,1.25,0,0,1,2.5,0V20.881A1.25,1.25,0,0,1,17.5,22.131Z"></path>
                        <path d="M17.5,22.693a3.189,3.189,0,0,1-2.262-.936L8.487,15.006a1.249,1.249,0,0,1,1.767-1.767l6.751,6.751a.7.7,0,0,0,.99,0l6.751-6.751a1.25,1.25,0,0,1,1.768,1.767l-6.752,6.751A3.191,3.191,0,0,1,17.5,22.693Z"></path>
                        <path d="M31.436,34.063H3.564A3.318,3.318,0,0,1,.25,30.749V22.011a1.25,1.25,0,0,1,2.5,0v8.738a.815.815,0,0,0,.814.814H31.436a.815.815,0,0,0,.814-.814V22.011a1.25,1.25,0,1,1,2.5,0v8.738A3.318,3.318,0,0,1,31.436,34.063Z"></path>
                    </svg>
                </span>
            </button>
        </div>
    );
}

export default Card2;
