import "./styles.css";
import React, { useEffect }  from "react";
import {Routes,Route } from "react-router-dom";
import Login from "./component/Login";
import Sign_user_interface from "./component/Sign_user_interface";
import LoginFireBase from "./component/LoginFireBase1";
import ContextProvider, { Nahdi_Gayth } from "./context/GlobalContext";
import { auth } from "./firebase123";







const App= () =>{



  const {dispatch} = Nahdi_Gayth()


  useEffect(()=>{
    auth.onAuthStateChanged((authUser)=>{
           if(authUser){
            dispatch({
              type:"SET_USER",
              user :authUser       
            })
           }
           else{
            dispatch({
              type:"SET_USER",
              user :null       
            })
           }
    })


  },[])












  return (
    <div className="App">

        <ContextProvider> 
        <Routes>

          <Route path="*" element={<>Something happend wrong :( </>}></Route>
          <Route path="/Sign-user-interface" element={<><Sign_user_interface/></>}></Route>
          <Route path="/" element={<><Sign_user_interface/></>}></Route>
          <Route path="/Login" element={<><Login/></>}></Route>
          <Route path="/LoginFireBase1" element={<><LoginFireBase/></>}></Route>

        </Routes>

        </ContextProvider>


    </div>
  );
}


export default App
