export const reducer = (state,action)=>{
 

     switch(action.type){
        case "SET_USER":
            return{
                ...state,
                user :action.user,
                img : action.img,
                pass:action.pass
            }

         case "PUSCH_TO_BASKET":
             {
                 return{
                     ...state,
                     Basket_Data : [...state.Basket_Data,action.payload]
                 }
             }
        default :
        return state

     }



}
