import React, { useEffect, useState } from 'react';
import './HomeUser1.css';

const HomeUser1 = () => {
 // Initialize state from local storage or default to "none"
 const [isOpen, setIsOpen] = useState(() => {
  return localStorage.getItem('isOpen') || "none";
});

// Update local storage whenever isOpen changes
useEffect(() => {
  localStorage.setItem('isOpen', isOpen);
}, [isOpen]);

// Toggle function to switch between "none" and "flex"
const handleToggle = () => {
  setIsOpen(prevState => prevState === "flex" ? "none" : "flex");
};

console.log(isOpen); // Log the current values for debugging
 

 
  return (
    <div className='Container-Screen-Shoot'>
      <div className='Screen'>
 
        <div className='banner-top' style={{ display: isOpen }}>


          <div className='banner-top-section1'>
            
            <div className='banner-top-section1-inner'>

               <div className='icon-section'>
                <img src='images/menu_63dp_FFFFFF_FILL0_wght400_GRAD0_opsz48.svg'/>
               </div>

               <div className='icon-section2'>
               <span class="material-symbols-outlined X">person</span>
               <span class="material-symbols-outlined X">image</span>
               <span class="material-symbols-outlined X">settings</span>
               <span class="material-symbols-outlined X">power_settings_new</span>
               </div>
               

              </div>

          </div>





          <div className='banner-top-section2'>x2</div>
          <div className='banner-top-section3'>X3</div>
        </div>







        <div className='banner' onClick={handleToggle}>
         <img src='/images/icons8-windows-8.svg' className='banner1'/>
        </div>
      </div>
    </div>
  );
};

export default HomeUser1;
